.scanner {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;

  &.slide-enter-active,
  &.slide-leave-active {
    transition: transform .5s;
  }
  &.slide-enter,
  &.slide-leave-to {
    transform: translate3d(0,100%,0);
  }
}

.qrcode-stream {
  width: 100%;
  height: 100%;

  &__inner-wrapper,
  &__camera {
    width: 100%;
    height: 100%;
  }
}
